// _________________________ Document Level Template  _________________________ 

// source: https://art-decor.org/art-decor/decor-templates--cdachvacd-?section=templates&id=2.16.756.5.30.1.1.10.1.16
// target: http://build.fhir.org/ig/hl7ch/ch-emed/StructureDefinition-ch-vacd-composition-immunization-administration.html
group CdaChVacdImmunizationAdministrationDocumentToBundle(source cda : ClinicalDocument, target bundle : Bundle) {
  cda ->  bundle.entry as e, 
             e.resource = create('Composition') as composition, 
             composition.id = uuid() as uuid,
             e.fullUrl = append('urn:uuid:',uuid),
             bundle.entry as e2, 
             e2.resource = create('Patient') as patient,patient share patient,
             patient.id = uuid() as uuid2,
             e2.fullUrl = append('urn:uuid:',uuid2)
             then ClinicalDocumentCdaChVacdImmunizationAdministrationDocumentToBundle(cda, patient, composition, bundle) "ClinicalDocumentToBody";
}

// source: https://art-decor.org/art-decor/decor-templates--cdachemed-?section=templates&id=2.16.756.5.30.1.1.10.1.16
// target: http://build.fhir.org/ig/hl7ch/ch-emed/StructureDefinition-ch-emed-composition-medicationcard.html
group ClinicalDocumentCdaChVacdImmunizationAdministrationDocumentToBundle(source cda : ClinicalDocument, target patient : Patient, target composition : Composition, target bundle : Bundle) extends ClinicalDocumentChToBundle {
  cda.component as component then {
    component.structuredBody as body then {
      body.component as component then {
        component.section as srcSection where (templateId.where(root='2.16.756.5.30.1.1.10.3.47')) -> composition.section as tgtSection then ImmunizationAdministrationSectionContentModule(srcSection, patient, tgtSection, bundle);
      } "component";
    } "body";
  } "component";
}

// _________________________ Section Level Templates _________________________ 

// source: http://build.fhir.org/ig/hl7ch/ch-emed/StructureDefinition-ch-emed-composition-medicationcard.html
// target: https://art-decor.org/art-decor/decor-templates--cdachemed-?section=templates&id=2.16.756.5.30.1.1.10.4.89
group ImmunizationAdministrationSectionContentModule(source src : Section, source patient : Patient, target tgt, target bundle: Bundle) extends ClinicalDocumentSection {
  src.entry as cdaEntry where (substanceAdministration.templateId.where(root='2.16.756.5.30.1.1.10.4.89')) ->  bundle.entry as e,  e.resource = create('Immunization') as immunization,  
    immunization.id = uuid() as uuid,
    e.fullUrl = append('urn:uuid:',uuid), 
    tgt.entry = create('Reference') as reference, reference.type='Immunization', reference.reference = append('urn:uuid:',uuid) then {
    cdaEntry.substanceAdministration as substanceAdministration then ImmunizationEntry(src, substanceAdministration, patient, immunization);
  } "cdaEntry";
}
